package dist.esper.core.flow.centralized;

/**
 * the centralized execution plan generated by @TreeBuilder.
 * @author tjy
 *
 */
public class Tree {
	RootNode root=null;
	long eplId;
	String epl;
	/** the cost computed by @TreeCostEvaluator */
	private double roughCost;
	
	public Tree(long eplId, String epl, RootNode root) {
		super();
		this.eplId = eplId;
		this.epl = epl;
		this.root = root;	
	}	
	
	public RootNode getRoot() {
		return root;
	}

	public void setRoot(RootNode root) {
		this.root = root;
	}
	
	public long getEplId() {
		return eplId;
	}

	public void setEplId(long eplId) {
		this.eplId = eplId;
	}

	public String getEpl() {
		return epl;
	}

	public void setEpl(String epl) {
		this.epl = epl;
	}

	@Override
	public String toString(){
		return root.toString();//FIXME
	}

	public double getRoughCost() {
		return roughCost;
	}

	public void setRoughCost(double roughCost) {
		this.roughCost = roughCost;
	}
	
}
