package dist.esper.core.flow.stream;

import java.io.Serializable;

import dist.esper.core.cost.DeltaResourceUsage;

/**
 * the distributed execution plan for single epl generated by @StreamTreeBuilder.
 * its based on the centralized execution plan @dist.esper.core.flow.centralized.Tree
 * @author tjy
 *
 */
public class StreamFlow implements Serializable {
	private static final long serialVersionUID = 4261941738009635097L;
	long eplId;
	String epl;
	RootStream rootStream=null;
	//DeltaResourceUsage rootDRU;

	public StreamFlow() {
		super();
	}

	public StreamFlow(long eplId, String epl, RootStream root) {
		super();
		this.eplId = eplId;
		this.epl = epl;
		this.rootStream = root;
	}
	
	@Override
	public String toString(){
		return rootStream.toString();//FIXME
	}

	public RootStream getRootStream() {
		return rootStream;
	}

	public void setRootStream(RootStream rootStream) {
		this.rootStream = rootStream;
	}

	public long getEplId() {
		return eplId;
	}

	public void setEplId(long eplId) {
		this.eplId = eplId;
	}

	public String getEpl() {
		return epl;
	}

	public void setEpl(String epl) {
		this.epl = epl;
	}

//	public DeltaResourceUsage getRootDRU() {
//		return rootDRU;
//	}
//
//	public void setRootDRU(DeltaResourceUsage rootDRU) {
//		this.rootDRU = rootDRU;
//	}
	
}
